package leetcode.medium;

import org.example.leetcode.medium.LongestPalindromicSubstring;
import org.junit.jupiter.api.Test;

import java.time.Duration;

import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertTimeout;

public class LongestPalindromicSubstringTest
{
    private LongestPalindromicSubstring lp = new LongestPalindromicSubstring();

    @Test
    void testOk1(){
        assertEquals("bab", lp.longestPalindrome("babas")); // "aba" is also correct

    }
    @Test
    void testOk()
    {
        // Normal cases
        assertEquals("bab", lp.longestPalindrome("babas")); // "aba" is also correct
        assertEquals("bb", lp.longestPalindrome("cbbd"));

        // Edge cases
        assertEquals("a", lp.longestPalindrome("a"));           // single character
        assertEquals("", lp.longestPalindrome(""));            // empty string
        assertEquals("aa", lp.longestPalindrome("aa"));        // two same characters

        // All same characters
        assertEquals("aaaaa", lp.longestPalindrome("aaaaa"));  // all same

        // Palindrome in the middle
        assertEquals("racecar", lp.longestPalindrome("aracecarx"));

        // Palindrome at the end
        assertEquals("level", lp.longestPalindrome("xlevel"));

        // No palindrome longer than 1
        assertEquals("x", lp.longestPalindrome("xyz"));

    }

    @Test
    void testLongString()
    {
        assertEquals(
                "",
                "");

    }

    @Test
    void testLostString2()
    {
        //stopped here. its too slow to pass.
        assertEquals(
                "",
                lp.longestPalindrome(
                        ""));

    }

    @Test
    void testSpeed1()
    {
        assertTimeout(Duration.ofMillis(300), () -> {
            lp.longestPalindrome(
                    "");

        });
    }
}
